document.addEventListener("DOMContentLoaded",()=>{let t=document.querySelectorAll(".page-index");let e=t[0].dataset.page;document.getElementById(e).classList.add("active");if(document.getElementById("clients-list")){const t=window.innerHeight,e=document.getElementById("clients-list").dataset.info,n=JSON.parse(e);window.addEventListener("scroll",function(){test=document.getElementById("otium-capital").getBoundingClientRect().top,console.log(test),n.forEach(e=>{target=document.getElementById(e);target.getBoundingClientRect().top<t-400&&target.getBoundingClientRect().top>0&&target.scrollIntoView({behavior:"smooth",block:"start"})})})}});